import React, { Component } from 'react';
import './Char.css';


const Char = props =>  {

    if (props.string.length > 0) {
    const stringList = (
            <ol>
                {props.string.split("").map(c => <li>c</li>)}
            </ol>
        );
    }     

    return (
      <div >
        {stringList ? stringList : null}
      </div>
    );

}

export default Char;
