import React, { Component } from 'react';
import './App.css';
import Input from "./UserInput/UserInput"
import UserOUput from './UserOutput/UserOutput';

class App extends Component {
  state = {
    users : [
      {name: "kareem", age: "22", caption: "i'm a web devoloper can i help you!j", id:"s;kl3"},
      {name: "khald", age: "34", caption: "i'm a doctor can i give you a medical consaltant", id:"adkj"},
      {name: "mo sala", age: "34", caption: "i'm a soccer player. hello word", id:"3fe"},
      {name: "kemo", age: "47", caption: "do you have any idea what do i do?", id:"b4d"},
    ]
  }; 

  render(){
    return (
      <div className="App">
        <Input />
        {
          this.state.users.map(() => {
            return (
              <UserOUput name={this.state.users.name} />
            )
          })
        }
      </div>
    );
  }
}

export default App;
